mapper.select:
SELECT om.id id, om.major_name majorName, om.major_name_eng majorNameEng, om.education_level educationLevel, om.major_type majorType, ou.university_name universityName, ou.university_name_eng universityNameEng, ou.logo_url logoUrl, ou.qs_rank qsRank, ou.country_name countryName, ou.province_name provinceName, ou.city_name cityName, oc.college_name collegeName, oc.college_name_eng collegeNameEng FROM osc_major om LEFT JOIN osc_university ou ON om.university_id = ou.id LEFT JOIN osc_college oc ON om.college_id = oc.id LEFT JOIN osc_area oa ON ou.area_id = oa.id
WHERE om.del_flag = 0
and om.university_id= #{param.universityId}
and om.college_id= #{param.collegeId}
and om.education_level= #{param.educationLevel}
and om.major_type= #{param.majorType}
and ou.area_id= #{param.areaId}
and ( om.major_name like concat('%', #{param.keyword},'%') or om.major_name_eng like concat('%', #{param.keyword},'%')
or ou.university_name like concat('%', #{param.keyword},'%') or ou.university_name_eng like concat('%', #{param.keyword},'%') or ou.country_name like concat('%', #{param.keyword},'%') or ou.province_name like concat('%', #{param.keyword},'%') or ou.city_name like concat('%', #{param.keyword},'%')
or oc.college_name like concat('%', #{param.keyword},'%') or oc.college_name_eng like concat('%', #{param.keyword},'%') )

SELECT om.id id, om.major_name majorName, om.major_name_eng majorNameEng, om.education_level educationLevel, om.major_type majorType, om.not_member_content notMemberContent, ou.id universityId, ou.university_name universityName, ou.university_name_eng universityNameEng, ou.logo_url logoUrl, ou.qs_rank qsRank, ou.country_name countryName, ou.province_name provinceName, ou.city_name cityName, oc.id collegeId, oc.college_name collegeName, oc.college_name_eng collegeNameEng, om.desc_content majorDescContent, om.requirement_content majorRequirementContent, om.member_content majorMemberContent, ou.image_url universityImageUrl, ou.content universityDescContent
FROM osc_major om LEFT JOIN osc_university ou ON om.university_id = ou.id LEFT JOIN osc_college oc ON om.college_id = oc.id LEFT JOIN osc_area oa ON ou.area_id = oa.id
WHERE om.del_flag = 0 and om.id= #{id}
